public class Ex21_GuGuDan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 2; i <= 19; i++) {
			for (int k = 1; k <= 19; k++) {
				System.out.println(i + "*" + k + "=" + i * k);
			}
			System.out.println("=============");
		}

	}

}
